return availableAdmins.map(admin => ({ json: admin }));

// Get all admins from previous node and filter for active ones only
const allAdmins = $('Process Admin Capacity').all().map(item => item.json);
const admins = allAdmins.filter(admin => {
  // Filter for admins with status = "active"
  return admin.status === 'active';
});

const pendingJobs = $('Get Pending Jobs').all().map(item => item.json);

// Debug logging
console.log(`Total admins from database: ${allAdmins.length}`);
console.log(`Active admins after filtering: ${admins.length}`);
console.log(`Pending jobs: ${pendingJobs.length}`);

// Log admin details for debugging
allAdmins.forEach(admin => {
  console.log(`Admin ${admin.id} (${admin.email}): status = "${admin.status}"`);
});

if (!pendingJobs.length) {
  console.log('No pending jobs found');
  return [];
}

if (!admins.length) {
  console.log('No active admins found - all admins have status other than "active"');  
  return [];
}

console.log(`Found ${admins.length} active admins and ${pendingJobs.length} pending jobs`);

const jobAssignments = [];

// SIMPLE ROUND-ROBIN ASSIGNMENT (only among ACTIVE admins)
for (let jobIndex = 0; jobIndex < pendingJobs.length && jobIndex < admins.length; jobIndex++) {
  const job = pendingJobs[jobIndex];
  const admin = admins[jobIndex % admins.length]; // Round-robin assignment among active admins only

  // Verify admin status is active before assignment (extra safety check)
  if (admin.status !== 'active') {
    console.log(`WARNING: Skipping non-active admin ${admin.id} (${admin.email}) with status: ${admin.status}`);
    continue;
  }

  // NO KEYWORD CHECKING - ANY ACTIVE ADMIN CAN TAKE ANY JOB
  jobAssignments.push({
    job_id: job.id,
    admin_id: admin.id,
    admin_email: admin.email,
    admin_name: admin.name,
    area_id: job.area_id,
    keyword: job.keyword,
    created_at: job.created_at,
    // Add completion webhook URL 
    completion_webhook: `${$vars.N8N_WEBHOOK_URL || 'http://localhost:5678/webhook'}/job-completion`
  });

  console.log(`âœ… Assigned job ${job.id} (${job.keyword}) to ACTIVE admin ${admin.email} (ID: ${admin.id}, status: ${admin.status})`);
}

console.log(`Total assignments made: ${jobAssignments.length}`);

// Final verification - log all assigned admin IDs
const assignedAdminIds = jobAssignments.map(assignment => assignment.admin_id);
console.log(`Jobs assigned to admin IDs: [${assignedAdminIds.join(', ')}]`);

return jobAssignments.map(assignment => ({ json: assignment }));

